import ReactMarkdown from 'react-markdown'
import Layout from './Layout';


const Post = ({post}) => {
    const authorName = post.author ? post.author.name : 'Unknown author'
    return(
        <div>
            <Layout />
            <h2>{post.title}</h2>
            <small>by {authorName}</small>
            <ReactMarkdown source={post.content} />
        </div>
    )
}

export default Post
