import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient()

export default async function handle(req, res) {
    const {name, email} = req.body
    const result = await prisma.user.findOne({
        where: {
            name: name,
            email: email,
        }
    })
    res.json(result)
}
